import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

function get() {
  return fetch(`/`, {
    accept: "application/json"
  })
    .then(response => JSON.parse(response.body));
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      message: ""
    }
  }

  componentDidMount(){
    this.setState({message: get()});
  }
  render() {
    return (
      <p>
        {this.state.message}
      </p>
    );
  }
}

export default App;
